mapper.select:
SELECT chat_gpt_id
FROM haru_chat_room_haru_tb
WHERE haru_no = #{haruNo} AND chat_room_no = #{chatRoomNo}

mapper.insert:
INSERT INTO haru_chat_message_tb (`chat_room_no` , `chat_msg_content`
,`chat_send_user_no`
, `chat_send_haru_no`
) VALUES (#{chatRoomNo},#{chatMsgContent}
,#{chatSendUserNo}
, (SELECT haru_no FROM HARU_CHAT_ROOM_HARU_TB WHERE chat_room_no = #{chatRoomNo} LIMIT 1)
)

mapper.update:
UPDATE haru_chat_room_tb
SET is_close='y'
WHERE chat_room_no = #{chatRoomNo}
